import React, { Component } from 'react';
import { Input, FormGroup, Label } from 'reactstrap';

import './index.css';

class ModeEvaluationApp extends Component {

  constructor(props) {
    super(props);
    this.state = {
      projectCapitalInvestment: 0, // 项目资金投入
      projectImportance: 0, // 项目重要程度
      projectEffectivenessEvaluationResults: 0, // 项目成效评价结果
      projectCompletionDegree: 0, // 所评价项目的规定时间完成程度
    };
  }

  render() {
    const { projectCapitalInvestment, projectImportance, projectEffectivenessEvaluationResults, projectCompletionDegree } = this.state;
    return (
      <div className="mode-evaluation-container w-100 h-100">
        <FormGroup key="project_capital_investment">
          <Label>{''}</Label>
          <Input
            type="number"
            value={projectCapitalInvestment}
            placeholder="请输入项目资金投入, 最小为0"
            min={0}
          />
        </FormGroup>
      </div>
    );
  }
}

export default ModeEvaluationApp;
